export function pushErrorNotification(err, toastr) {
  if (toastr != null) {
    toastr.error(err.toString(), 'Error Message', {
      timeOut: 3000
    });
  } else {
    console.error('An Error occurred and could not be displayed via toast! Details => ' + JSON.stringify(err));
  }
}
export function pushInfoNotification(msg, toastr) {
  if (toastr != null) {
    toastr.info(msg, 'Information', {
      timeOut: 3000
    });
  } else {
    console.log('An informational event/msg was received and could not be displayed via toast! Details => ' + JSON.stringify(msg));
  }
}
